void dfs(int x, int y, int cnt) {
    if (cnt > 20) return;

    if (x == h - 1) {
        ans = min(ans, cnt);
        return;
    }
    rep(i, 8) {
        int nx = x + dx[i];
        int ny = y + dy[i];
        if (nx < 0 || nx >= h || ny < 0 || ny >= w) continue;
        if (s[nx][ny] != 'X') continue;
