ll expmod(ll x, ll n, ll m) {
    ll ans = 1;
    for (; n;) {
        if (n & 1) ans = (ans * x) % m;
        x = (x * x) % m;
        n >>= 1;
    }
    return ans;
}

ll combmod(ll n, ll k, ll m) {
    ll ret = 1;
    ll div = 1;
    for (ll i = 0; i < k; ++i) {
        ret = (ret * (n - i) % m) % m;
        div = (div * (i + 1)) % m;
    }
    div = expmod(div, m - 2, m) % m;
    return (ret * div) % m;
}

