struct combination {
    vector<mint> fact, ifact;
    combination(ll n) : fact(n + 1), ifact(n + 1) {
        assert(n < mod);
        fact[0] = 1;
        for (ll i = 1; i <= n; ++i) fact[i] = fact[i - 1] * i;
        ifact[n] = fact[n].inv();
        for (ll i = n; i >= 1; --i) ifact[i - 1] = ifact[i] * i;
    }
    mint operator()(ll n, ll k) {
        if (k < 0 || k > n) return 0;
        return fact[n] * ifact[k] * ifact[n - k];
    }
} comb(1000005);
mint f(ll n, ll k) {
    if (n < 0) return 0;

    mint res = comb(n, k);
    res *= comb.fact[k];
    return res;
}

