                    for (int use = 0; use < b[i - 1]; use++) {
                        if (cnt - use >= 0) {
                            Int tmp = nCk(cnt, use) % MOD * pw % MOD;
                            dp[asum][cnt][p][0] +=
                                dp[asum - a[i - 1]][cnt - use][p ^ 1][0] * tmp %
                                MOD;
                            dp[asum][cnt][p][1] +=
                                dp[asum - a[i - 1]][cnt - use][p ^ 1][1] * tmp %
