#define INF 1145141919
#define MOD 1000000007
#define MAX_L 22
int N;
vector<P> G[100000];
int U[MAX_L][100000], R[100000];
long long L[100000];

void dfs(int x, int p, int r, long long l) {
    U[0][x] = p;
    R[x] = r;
    L[x] = l;
    for (P pp : G[x]) {
        int t = pp._1;
        if (t == p) continue;
        dfs(t, x, r + 1, l + pp._2);
    }
}

int lca(int a, int b) {
    if (R[a] > R[b]) swap(a, b);
    for (int k = 0; k < MAX_L; k++) {
        if ((R[a] - R[b]) >> k & 1) b = U[k][b];
    }
    if (a == b) return a;
    for (int k = MAX_L - 1; k >= 0; k--) {
        if (U[k][a] != U[k][b]) {
            a = U[k][a];
            b = U[k][b];
        }
    }
    return U[0][a];
}
long long dist(int x, int y) {
    int g = lca(x, y);
    return L[x] + L[y] - 2LL * L[g];
}

