        G[b].pb(P(a, c));
    }
    rep(i, MAX_L) rep(j, N) U[i][j] = -1;
    dfs(0, -1, 0, 0);
    for (int i = 0; i < MAX_L - 1; i++) {
        for (int v = 0; v < N; v++) {
            if (U[i][v] == -1) U[i + 1][v] = -1;
            U[i + 1][v] = U[i][U[i][v]];
        }
    }
    int Q, K;
    cin >> Q >> K;
    K--;
    rep(i, Q) {
        int x, y;
        cin >> x >> y;
        x--, y--;
        cout << dist(x, K) + dist(K, y) << "\n";
    }
    return 0;
}

