#include <algorithm>
#include <cmath>
#include <cstdio>
#include <cstring>
#include <iostream>
#include <queue>
#include <set>
#include <vector>

using namespace std;

const int maxn = 1e5 + 5;

struct Node {
    int x, y, id;
} node[maxn];

struct Edge {
    int u, v, val;
    bool operator<(const Edge &rhs) const { return val < rhs.val; }
} e[maxn << 1];

int par[maxn];
int n;

bool cmp1(const Node &a, const Node &b) { return a.x < b.x; }

bool cmp2(const Node &a, const Node &b) { return a.y < b.y; }

void init() {
    for (int i = 0; i <= n; i++) par[i] = i;
}

int findpar(int x) { return par[x] = (par[x] == x ? x : findpar(par[x])); }

void unite(int x, int y) {
    x = findpar(x), y = findpar(y);
    if (x == y) return;
    par[x] = y;
}

int main(int argc, char const *argv[]) {
    cin >> n;

    init();

    for (int i = 1; i <= n; i++) {
        scanf("%d%d", &node[i].x, &node[i].y);
        node[i].id = i;
    }

    sort(node + 1, node + n + 1, cmp1);

    int cnt = 0;
    for (int i = 2; i <= n; i++)
        e[cnt++] =
            (Edge){node[i - 1].id, node[i].id, node[i].x - node[i - 1].x};

    sort(node + 1, node + n + 1, cmp2);
    for (int i = 2; i <= n; i++)
        e[cnt++] =
            (Edge){node[i - 1].id, node[i].id, node[i].y - node[i - 1].y};

    sort(e, e + cnt);

    long long ans = 0;
    for (int i = 0; i < cnt; i++) {
        int u = e[i].u, v = e[i].v, val = e[i].val;
        if (findpar(u) == findpar(v)) continue;
        unite(u, v);
        ans += val;
    }

    cout << ans << endl;

    return 0;
}

