        }

        for (vector<edge>::iterator edgeptr = edges.begin();
             edgeptr != edges.end(); edgeptr++) {
            distances[edgeptr->target] =
                MIN(distances[edgeptr->source] + edgeptr->weight,
                    distances[edgeptr->target]);
        }

        bool same = true;
        for (int j = 0; j < V; j++) {
            if (distances[j] != prev_distances[j]) {
                same = false;
                break;
