void visit() {
    int dx[4] = {0, 1, 0, -1};
    int dy[4] = {-1, 0, 1, 0};
    int sum, flg = 0;
    int x = 2, y = n;
    map[y][x] = ' ';
    int ab[4] = {2, 2, 2, 2};

    while (1) {
        sum = 0;
        while (1) {
            if (map[y + dy[flg] * 2][x + dx[flg] * 2] != '#' &&
                map[y + dy[flg]][x + dx[flg]] != ' ') {
                flg++;

                break;
            }

            map[y + dy[flg]][x + dx[flg]] = ' ';
            y = y + dy[flg];
            x = x + dx[flg];
            sum++;
        }
        if (flg == 4) flg = 0;
        if (sum == 0) break;
        ab[0] = ab[1];
        ab[1] = ab[2];
        ab[2] = sum;
        if (ab[0] == 1 && ab[2] == 1 && ab[1] == 1) break;
    }
}

