        for (int i = 0; i <= n + 1; i++) {
            map[i].resize(n + 2);
            for (int j = 0; j <= n + 1; j++) {
                map[i][j] = '#';
                if (i == 0 || j == 0 || i == n + 1 || j == n + 1)
                    map[i][j] = '.';
            }
        }

        visit();

